import React from 'react';
import './App.css';
import climbingImage from './assets/frontpage.png'; // Assure-toi que l'image soit dans le bon dossier

function App() {
  const handleClick = (url) => {
    window.location.href = url;
  };

  return (
    <div className="App">
      <img className="full-screen-image" src={climbingImage} alt="Climbing App Frontpage" />

      {/* Bloc cliquable 1 */}
      <div className="clickable-block" onClick={() => handleClick('https://example.com/climbers')}>
        <h2>Explore Climbers</h2>
      </div>

      {/* Bloc cliquable 2 */}
      <div className="clickable-block" onClick={() => handleClick('https://example.com/routes')}>
        <h2>Explore Routes</h2>
      </div>
    </div>
  );
}

export default App;
