import React from 'react';
import './App.css';
import climbingImage from './assets/frontpage.png';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

// Tes pages individuelles
function Home() {
  return (
    <div className="App">
      <img className="full-screen-image" src={climbingImage} alt="Climbing App Frontpage" />
      <div className="overlay">
        {/* Le lien "Dashboard" utilise React Router pour la navigation interne */}
        <Link to="/dashboard" className="clickable-block">
          <h2>Dashboard</h2>
        </Link>
        {/* Les liens "API" et "GitHub" ouvrent des pages externes dans un nouvel onglet */}
        {/* En local remplacer http://localhost:5000/docs par http://127.0.0.1:8000/docs */}
        <a href="http://127.0.0.1:8000/docs" className="clickable-block" target="_blank" rel="noopener noreferrer">  
          <h2>API</h2>
        </a>
        <a href="https://github.com/LucienLaumont" className="clickable-block" target="_blank" rel="noopener noreferrer">
          <h2>GitHub</h2>
        </a>
      </div>
    </div>
  );
}

function Dashboard() {
  const [countries, setCountries] = useState([]);
  const [climbersData, setClimbersData] = useState({});
  const [loading, setLoading] = useState(true);

  // Récupération des pays via l'API
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/climbers/countries/')
      .then(response => {
        setCountries(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Erreur lors de la récupération des pays", error);
        setLoading(false);
      });
  }, []);

  // Récupération du nombre de grimpeurs pour chaque pays
  useEffect(() => {
    if (countries.length > 0) {
      let climbersCount = {};

      const fetchData = async () => {
        for (let country of countries) {
          try {
            const response = await axios.get(`http://127.0.0.1:8000/climbers/by_country/?country=${country}`);
            climbersCount[country] = response.data.length; // Nombre de grimpeurs pour chaque pays
          } catch (error) {
            console.error(`Erreur lors de la récupération des grimpeurs pour ${country}`, error);
            climbersCount[country] = 0; // En cas d'erreur, mettre à 0
          }
        }

        setClimbersData(climbersCount);
      };

      fetchData();
    }
  }, [countries]);

  if (loading) {
    return <div>Chargement des données...</div>;
  }

  // Préparation des données pour le graphique
  const data = {
    labels: Object.keys(climbersData), // Les pays
    datasets: [
      {
        label: 'Nombre de grimpeurs par pays',
        data: Object.values(climbersData), // Nombre de grimpeurs pour chaque pays
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div>
      <h2>Statistiques des grimpeurs par pays</h2>
      <Bar data={data} />
    </div>
  );
}

// Configuration du Router
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
}

export default App;
