import React, { useState } from 'react';

function AgeRangeSelector({ onAgeChange }) {
  const [minAge, setMinAge] = useState(18);
  const [maxAge, setMaxAge] = useState(60);

  const handleMinAgeChange = (event) => {
    const value = parseInt(event.target.value, 10);
    setMinAge(value);
    onAgeChange(value, maxAge); // Informe le parent du changement
  };

  const handleMaxAgeChange = (event) => {
    const value = parseInt(event.target.value, 10);
    setMaxAge(value);
    onAgeChange(minAge, value); // Informe le parent du changement
  };

  return (
    <div className="age-range-selector">
      <div className="age-input">
        <label>Âge minimum :</label>
        <input 
          type="range" 
          min="18" 
          max="100" 
          value={minAge} 
          onChange={handleMinAgeChange} 
        />
        <span>{minAge}</span>
      </div>
      <div className="age-input">
        <label>Âge maximum :</label>
        <input 
          type="range" 
          min="18" 
          max="100" 
          value={maxAge} 
          onChange={handleMaxAgeChange} 
        />
        <span>{maxAge}</span>
      </div>
    </div>
  );
}

export default AgeRangeSelector;
